// video-blob.js

// Video: https://github.com/mathiasbynens/small
const VIDEO = 'data:video/mp4;base64,'

// base64toBlob: https://github.com/video-dev/can-autoplay
function base64toBlob(base64) {
  let base64Regex = /^data:([^;]+);base64,(.+)$/i
  let matches = base64.match(base64Regex)
  let contentType = matches[1]
  let base64Data = matches[2]

  let sliceSize = 1024
  let byteCharacters = atob(base64Data)
  let bytesLength = byteCharacters.length
  let slicesCount = Math.ceil(bytesLength / sliceSize)
  let byteArrays = new Array(slicesCount)

  for (let sliceIndex = 0; sliceIndex < slicesCount; ++sliceIndex) {
    let begin = sliceIndex * sliceSize
    let end = Math.min(begin + sliceSize, bytesLength)

    let bytes = new Array(end - begin)
    for (let offset = begin, i = 0; offset < end; ++i, ++offset) {
      bytes[i] = byteCharacters[offset].charCodeAt(0)
    }
    byteArrays[sliceIndex] = new Uint8Array(bytes)
  }
  return new Blob(byteArrays, { type: contentType })
}

let blob = base64toBlob(VIDEO)

let sourceObj = {
  source: URL.createObjectURL(blob),
  mimeType: blob.type
}

export default function () {
  return sourceObj
}
